                                elocation   =event.latLng;
	                            latitud     = new String(event.latLng.lng());
	                            longitud    = new String(event.latLng.lat());	                            
	                            coordinate  ={latitude:latitud,longitude:longitud};
	                            limpiar_virtual();
                                marker          =markerMap(elocation);
	                            locationsMarker.push(marker);						
                            });        
